mapper.select:
SELECT COUNT(*)
FROM   pro_user
WHERE  user_id = #{user_id}

SELECT *
FROM   pro_user
WHERE  user_id = #{user_id}
AND    user_pw = #{user_pw}

mapper.update:
UPDATE pro_user
SET    user_pw = #{user_pw},
       user_nick = #{user_nick}
WHERE  user_no = #{user_no}

mapper.select:
SELECT user_no
FROM pro_user
WHERE user_id = #{user_id}

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY user_no DESC)rn, user_no, user_id, user_pw, user_name, user_nick, user_email, user_birth, user_phone FROM pro_user
WHERE ${type} like '%'||#{keyword}||'%'
)a WHERE a.rn > #{startRn} AND a.rn <= #{endRn}
