mapper.select:
SELECT COUNT(*)
FROM   pro_user
WHERE  user_id = #{user_id}

SELECT *
FROM   pro_user
WHERE  user_id = #{user_id}
AND    user_pw = #{user_pw}

mapper.update:
UPDATE pro_user
SET    user_pw = #{user_pw},
       user_nick = #{user_nick}
WHERE  user_no = #{user_no}

mapper.select:
SELECT user_no
FROM pro_user
WHERE user_id = #{user_id}
